'use strict';

var uniform = require( '@stdlib/random/array/uniform' );
var dgemm = require( './../lib' );

var M = 2;
var N = 3;
var K = 3;

var A = uniform( M * K, 1.0, 10.0, {
	'dtype': 'float64'
});

var B = uniform( K * N, 1.0, 10.0, {
	'dtype': 'float64'
});

var C = uniform( M * N, 1.0, 10.0, {
	'dtype': 'float64'
});

var out = dgemm( 'row-major', 'none', 'none', 2, 3, 3, 5.0, A, 3, B, 3, 6.0, C, 3 );
console.log( out );
