'use strict';

// MAIN //

/**
* Solves matrix equation `op(A) * X = alpha * B` or `X * op(A) = alpha * B` where `alpha` is a scalar, `X` and `B` are m by n matrices, `A` is a unit, or non-unit,  upper or lower triangular matrix  and  `op( A )`  is one  of `op( A) = A` or `op( A ) = A**T`. The matrix `X` is overwritten on `B`.
*
* @private
* @param {string} order - specifies the memory layout of `A` and `B`
* @param {string} side - specifies whether `op( A )` appears on the left or right of `X`
* @param {string} uplo - specifies whether the matrix `A` is an upper or lower triangular matrix
* @param {string} transa - specifies the form of `op( A )` to be used in matrix multiplication
* @param {string} diag - specifies whether or not `A` is unit triangular
* @param {NonNegativeInteger} m - number of rows in `B`
* @param {NonNegativeInteger} n - number of columns in `B`
* @param {number} alpha - scalar alpha
* @param {Float64Array} A - input matrix `A`
* @param {NonNegativeInteger} LDA - stride of the first dimension of `A` (a.k.a., leading dimension of the matrix `A`)
* @param {NonNegativeInteger} offsetA - index offset for matrix `A`
* @param {Float64Array} B - input matrix `B`
* @param {NonNegativeInteger} LDB - stride of the first dimension of `B` (a.k.a., leading dimension of the matrix `B`)
* @param {NonNegativeInteger} offsetB - index offset for matrix `B`
* @returns {Float64Array} matrix `B`
*
* @example
* TODO:
*/
function dtrsm( order, side, uplo, transa, diag, m, n, alpha, A, LDA, offsetA, B, LDB, offsetB ) { // eslint-disable-line max-len
	var nounit;
	var lside;
	var upper;
	var tmp;
	var i;
	var j;
	var k;

	lside = side === 'L';
	nounit = diag === 'N';
	upper = uplo === 'U';

	// TODO: remove it
	offsetB = offsetA;
	offsetA = offsetB;

	if ( m === 0 || n === 0 ) {
		return B;
	}
	if ( alpha === 0.0 ) {
		if ( order === 'row-major' ) {
			for ( j = 0; j < n; j++ ) {
				for ( i = 0; i < m; i++ ) {
					B[ ( i * LDB ) + j ] = 0.0;
				}
			}
			return B;
		}
		for ( j = 0; j < n; j++ ) {
			for ( i = 0; i < m; i++ ) {
				B[ ( j * LDB ) + i ] = 0.0;
			}
		}
		return B;
	}
	if ( lside ) {
		if ( transa === 'N' ) {
			// B := alpha * inv( A ) * B
			if ( upper ) {
				if ( order === 'column-major' ) {
					for ( j = 0; j < n; j++ ) {
						if ( alpha !== 1 ) {
							for ( i = 0; i < m; i++ ) {
								B[ ( j * LDB ) + i ] *= alpha;
							}
						}
						for ( k = m - 1; k >= 0; k-- ) {
							if ( B[ ( j * LDB ) + k ] !== 0 ) {
								if ( nounit ) {
									B[ ( j * LDB ) + k ] /= A[ ( k * LDA ) + k ];
								}
								for ( i = 0; i < k; i++ ) {
									B[ ( j * LDB ) + i ] -= B[ ( j * LDB ) + k ] * A[ ( k * LDA ) + i ];
								}
							}
						}
					}
					return B;
				}
				// order === "row-major"
				for ( j = 0; j < n; j++ ) {
					if ( alpha !== 1 ) {
						for ( i = 0; i < m; i++ ) {
							B[ ( i * LDB ) + j ] *= alpha;
						}
					}
					for ( k = 0; k < m; k++ ) {
						if ( B[ ( k * LDB ) + j ] !== 0 ) {
							if ( nounit ) {
								B[ ( k * LDB ) + j ] /= A[ ( k * LDA ) + k ];
							}
							for ( i = k + 1; i < m; i++ ) {
								B[ ( i * LDB ) + j ] -= B[ ( k * LDB ) + j ] * A[ ( i * LDA ) + k ];
							}
						}
					}
				}
				return B;
			}
			// lower
			if ( order === 'column-major' ) {
				for ( j = 0; j < n; j++ ) {
					if ( alpha !== 1 ) {
						for ( i = 0; i < m; i++ ) {
							B[ ( j * LDB ) + i ] *= alpha;
						}
					}
					for ( k = 0; k < m; k++ ) {
						if ( B[ ( j * LDB ) + k ] !== 0 ) {
							if ( nounit ) {
								B[ ( j * LDB ) + k ] /= A[ ( k * LDA ) + k ];
							}
							for ( i = k; i < m; i++ ) {
								B[ ( j * LDB ) + i ] -= B[ ( j * LDB ) + k ] * A[ ( k * LDA ) + i ];
							}
						}
					}
				}
				return B;
			}
			// order === "row-major"
			for ( j = 0; j < n; j++ ) {
				if ( alpha !== 1 ) {
					for ( i = 0; i < m; i++ ) {
						B[ ( i * LDB ) + j ] *= alpha;
					}
				}
				for ( k = m - 1; k >= 0; k-- ) {
					if ( B[ ( k * LDB ) + j ] !== 0 ) {
						if ( nounit ) {
							B[ ( k * LDB ) + j ] /= A[ ( k * LDA ) + k ];
						}
						for ( i = 0; i < k - 1; i++ ) {
							B[ ( i * LDB ) + j ] -= B[ ( k * LDB ) + j ] * A[ ( i * LDA ) + k ];
						}
					}
				}
			}
			return B;
		}
		// B := alpha * inv( A**T ) * B
		if ( upper ) {
			if ( order === 'column-major' ) {
				for ( j = 0; j < n; j++ ) {
					for ( i = 0; i < m; i++ ) {
						tmp = alpha * B[ ( j * LDB ) + i ];
						for ( k = 0; k < i - 1; k++ ) {
							tmp -= A[ ( i * LDA ) + k ] * B[ ( j * LDB ) + k ];
						}
						if ( nounit ) {
							tmp /= A[ ( i * LDA ) + i ];
						}
						B[ ( j * LDB ) + i ] = tmp;
					}
				}
				return B;
			}
			// order === "row-major"
			for ( j = 0; j < n; j++ ) {
				for ( i = m - 1; i >= 0; i-- ) {
					tmp = alpha * B[ ( i * LDB ) + j ];
					for ( k = i; k < m; k++ ) {
						tmp -= A[ ( k * LDA ) + i ] * B[ ( k * LDB ) + j ];
					}
					if ( nounit ) {
						tmp /= A[ ( i * LDA ) + i ];
					}
					B[ ( i * LDB ) + j ] = tmp;
				}
			}
			return B;
		}
		// lower
		if ( order === 'column-major' ) {
			for ( j = 0; j < n; j++ ) {
				for ( i = m - 1; i >= 0; i-- ) {
					tmp = alpha * B[ ( j * LDB ) + i ];
					for ( k = i; k < m; k++ ) {
						tmp -= A[ ( i * LDA ) + k ] * B[ ( j * LDB ) + k ];
					}
					if ( nounit ) {
						tmp /= A[ ( i * LDA ) + i ];
					}
					B[ ( j * LDB ) + i ] = tmp;
				}
			}
			return B;
		}
		// order === "row-major"
		for ( j = 0; j < n; j++ ) {
			for ( i = 0; i < m; i++ ) {
				tmp = alpha * B[ ( i * LDB ) + j ];
				for ( k = 1; k < i - 1; k++ ) {
					tmp -= A[ ( k * LDA ) + i ] * B[ ( k * LDB ) + j ];
				}
				if ( nounit ) {
					tmp /= A[ ( i * LDA ) + i ];
				}
				B[ ( i * LDB ) + j ] = tmp;
			}
		}
		return B;
	}
	// !lside
	if ( transa === 'N' ) {
		// B := alpha * B * inv( A )
		if ( upper ) {
			if ( order === 'column-major' ) {
				for ( j = 0; j < n; j++ ) {
					if ( alpha !== 1 ) {
						for ( i = 0; i < m; i++ ) {
							B[ ( j * LDB ) + i ] *= alpha;
						}
					}
					for ( k = 0; k < j - 1; k++ ) {
						if ( A[ ( j * LDA ) + k ] !== 0.0 ) {
							for ( i = 0; i < m; i++ ) {
								B[ ( j * LDB ) + i ] -= A[ ( j * LDA ) + k ] * B[ ( k * LDB ) + i ];
							}
						}
					}
					if ( nounit ) {
						tmp = 1.0 / A[ ( j * LDA ) + j ];
						for ( i = 0; i < m; i++ ) {
							B[ ( j * LDB ) + i ] *= tmp;
						}
					}
				}
				return B;
			}
			// order === "row-major"
			for ( j = n - 1; j >= 0; j-- ) {
				if ( alpha !== 1 ) {
					for ( i = 0; i < m; i++ ) {
						B[ ( i * LDB ) + j ] *= alpha;
					}
				}
				for ( k = j; k < n; k++ ) {
					if ( A[ ( k * LDA) + j ] !== 0.0 ) {
						for ( i = 0; i < m; i++ ) {
							B[ ( i * LDB ) + j ] -= A[ ( k * LDA ) + j ] * B[ ( i * LDB ) + k ];
						}
					}
				}
				if ( nounit ) {
					tmp = 1.0 / A[ ( j * LDA ) + j ];
					for ( i = 0; i < m; i++ ) {
						B[ ( i * LDB ) + j ] *= tmp;
					}
				}
			}
		}
		// lower
		if ( order === 'column-major' ) {
			for ( j = n - 1; j >= 0; j-- ) {
				if ( alpha !== 1 ) {
					for ( i = 0; i < m; i++ ) {
						B[ ( j * LDB ) + i ] *= alpha;
					}
				}
				for ( k = j; k < n; k++ ) {
					if ( A[ ( j * LDA ) + k ] !== 0 ) {
						for ( i = 0; i < m; i++ ) {
							B[ ( j * LDB ) + i ] -= A[ ( j * LDA ) + k ] * B[ ( k * LDB ) + i ];
						}
					}
				}
				if ( nounit ) {
					tmp = 1.0 / A[ ( j * LDA ) + j ];
					for ( i = 0; i < m; i++ ) {
						B[ ( j * LDB ) + i ] *= tmp;
					}
				}
			}
			return B;
		}
		// order === "row-major"
		for ( j = 0; j < n; j++ ) {
			if ( alpha !== 1.0 ) {
				for ( i = 0; i < m; i++ ) {
					B[ ( i * LDB ) + j ] *= alpha;
				}
			}
			for ( k = 0; k < j - 1; k++ ) {
				if ( A[ ( k * LDA ) + j ] !== 0.0 ) {
					for ( i = 0; i < m; i++ ) {
						B[ ( i * LDB ) + j ] -= A[ ( k * LDA ) + j ] * B[ ( i * LDB ) + k ];
					}
				}
			}
			if ( nounit ) {
				tmp = 1.0 / A[ ( j * LDA ) + j ];
				for ( i = 0; i < m; i++ ) {
					B[ ( i * LDB ) + j ] *= tmp;
				}
			}
		}
		return B;
	}
	// B := alpha * B * inv( A**T )
	if ( upper ) {
		if ( order === 'column-major' ) {
			for ( k = n - 1; k >= 0; k-- ) {
				if ( nounit ) {
					tmp = 1.0 / A[ ( k * LDA ) + k ];
					for ( i = 0; i < m; i++ ) {
						B[ ( k * LDB ) + i ] *= tmp;
					}
				}
				for ( j = 0; j < k - 1; j++ ) {
					if ( A[ ( k * LDA ) + j ] !== 0 ) {
						tmp = A[ ( k * LDA ) + j ];
						for ( i = 0; i < m; i++ ) {
							B[ ( j * LDB ) + i ] -= tmp * B[ ( k * LDB ) + i ];
						}
					}
				}
				if ( alpha !== 1 ) {
					for ( i = 0; i < m; i++ ) {
						B[ ( k * LDB ) + i ] *= alpha;
					}
				}
			}
			return B;
		}
		// order === "row-major"
		for ( k = 0; k < n; k++ ) {
			if ( nounit ) {
				tmp = 1.0 / A[ ( k * LDA ) + k ];
				for ( i = 0; i < m; i++ ) {
					B[ ( i * LDB ) + k ] *= tmp;
				}
			}
			for ( j = k; j < n; j++ ) {
				if ( A[ ( j * LDA ) + k ] !== 0 ) {
					tmp = A[ ( j * LDA ) + k ];
					for ( i = 0; i < m; i++ ) {
						B[ ( i * LDB ) + j ] -= tmp * B[ ( i * LDB ) + k ];
					}
				}
			}
			if ( alpha !== 1 ) {
				for ( i = 0; i < m; i++ ) {
					B[ ( i * LDB ) + k ] *= alpha;
				}
			}
		}
		return B;
	}
	// lower
	if ( order === 'column-major' ) {
		for ( k = 0; k < n; k++ ) {
			if ( nounit ) {
				tmp = 1.0 / A[ ( k * LDA ) + k ];
				for ( i = 0; i < m; i++ ) {
					B[ ( k * LDB ) + i ] *= tmp;
				}
			}
			for ( j = k; j < n; j++ ) {
				if ( A[ ( k * LDA ) + j ] !== 0 ) {
					tmp = A[ ( k * LDA ) + j ];
					for ( i = 0; i < m; i++ ) {
						B[ ( j * LDB ) + i ] -= tmp * B[ ( k * LDB ) + i ];
					}
				}
			}
			if ( alpha !== 1 ) {
				for ( i = 0; i < m; i++ ) {
					B[ ( k * LDB ) + i ] *= alpha;
				}
			}
		}
		return B;
	}
	// order === "row-major"
	for ( k = n - 1; k >= 0; k-- ) {
		if ( nounit ) {
			tmp = 1.0 / A[ ( k * LDA ) + k ];
			for ( i = 0; i < m; i++ ) {
				B[ ( i * LDB ) + k ] *= tmp;
			}
		}
		for ( j = 0; j < k - 1; j++ ) {
			if ( A[ ( j * LDA ) + k ] !== 0 ) {
				tmp = A[ ( j * LDA ) + k ];
				for ( i = 0; i < m; i++ ) {
					B[ ( i * LDB ) + j ] -= tmp * B[ ( i * LDB ) + k ];
				}
			}
		}
		if ( alpha !== 1 ) {
			for ( i = 0; i < m; i++ ) {
				B[ ( i * LDB ) + k ] *= alpha;
			}
		}
	}
	return B;
}


// EXPORTS //

module.exports = dtrsm;
