'use strict';

// MAIN //

/**
* Solves matrix equation `op(A) * X = alpha * B` or `X * op(A) = alpha * B` where `alpha` is a scalar, `X` and `B` are m by n matrices, `A` is a unit, or non-unit,  upper or lower triangular matrix  and  `op( A )`  is one  of `op( A) = A` or `op( A ) = A**T`. The matrix `X` is overwritten on `B`.
*
* @private
* @param {string} order - specifies the memory layout of `A` and `B`
* @param {string} side - specifies whether `op( A )` appears on the left or right of `X`
* @param {string} uplo - specifies whether the matrix `A` is an upper or lower triangular matrix
* @param {string} transa - specifies the form of `op( A )` to be used in matrix multiplication
* @param {string} diag - specifies whether or not `A` is unit triangular
* @param {NonNegativeInteger} m - number of rows in `B`
* @param {NonNegativeInteger} n - number of columns in `B`
* @param {number} alpha - scalar alpha
* @param {Float64Array} A - input matrix `A`
* @param {NonNegativeInteger} LDA - stride of the first dimension of `A` (a.k.a., leading dimension of the matrix `A`)
* @param {NonNegativeInteger} offsetA - index offset for matrix `A`
* @param {Float64Array} B - input matrix `B`
* @param {NonNegativeInteger} LDB - stride of the first dimension of `B` (a.k.a., leading dimension of the matrix `B`)
* @param {NonNegativeInteger} offsetB - index offset for matrix `B`
* @returns {Float64Array} matrix `B`
*
* @example
* var Float64Array = require( '@stdlib/array/float64' );
*
* var A = new Float64Array( [ 1.0, 3.0, 0.0, 4.0 ] );
* var B = new Float64Array( [ 5.0, 7.0, 0.0, 8.0 ] );
*
* dtrsm( 'row-major', 'left', 'upper', 'none', 'non-unit', 2, 2, 6.0, A, 2, 0, B, 2, 0 );
* // B => <Float64Array>[ 30.0, 6.0, 0.0, 12.0 ]
*/
function dtrsm( order, side, uplo, transa, diag, m, n, alpha, A, LDA, offsetA, B, LDB, offsetB ) { // eslint-disable-line max-len, max-params
	var nounit;
	var lside;
	var upper;
	var tmp;
	var i;
	var j;
	var k;

	lside = side === 'left';
	nounit = diag === 'non-unit';
	upper = uplo === 'upper';

	if ( m === 0 || n === 0 ) {
		return B;
	}
	if ( order === 'row-major' ) {
		lside = !lside;
		upper = !upper;
		order = 'column-major';
	}
	if ( alpha === 0.0 ) {
		for ( j = 0; j < n; j++ ) {
			for ( i = 0; i < m; i++ ) {
				B[ offsetB + ( j * LDB ) + i ] = 0.0;
			}
		}
		return B;
	}
	if ( lside ) {
		if ( transa === 'none' ) {
			// B := alpha * inv( A ) * B
			if ( upper ) {
				for ( j = 0; j < n; j++ ) {
					if ( alpha !== 1.0 ) {
						for ( i = 0; i < m; i++ ) { // eslint-disable-line max-depth
							B[ offsetB + ( j * LDB ) + i ] *= alpha;
						}
					}
					for ( k = m - 1; k >= 0; k-- ) {
						if ( B[ offsetB + ( j * LDB ) + k ] !== 0 ) { // eslint-disable-line max-depth
							if ( nounit ) { // eslint-disable-line max-depth
								B[ offsetB + ( j * LDB ) + k ] /= A[ offsetA + ( k * LDA ) + k ];
							}
							for ( i = 0; i < k; i++ ) { // eslint-disable-line max-depth
								B[ offsetB + ( j * LDB ) + i ] -= B[ offsetB + ( j * LDB ) + k ] * A[ offsetA + ( k * LDA ) + i ];
							}
						}
					}
				}
				return B;
			}
			// lower
			for ( j = 0; j < n; j++ ) {
				if ( alpha !== 1.0 ) {
					for ( i = 0; i < m; i++ ) {
						B[ offsetB + ( j * LDB ) + i ] *= alpha;
					}
				}
				for ( k = 0; k < m; k++ ) {
					if ( B[ offsetB + ( j * LDB ) + k ] !== 0 ) {
						if ( nounit ) { // eslint-disable-line max-depth
							B[ offsetB + ( j * LDB ) + k ] /= A[ offsetA + ( k * LDA ) + k ];
						}
						for ( i = k + 1; i < m; i++ ) { // eslint-disable-line max-depth
							B[ offsetB + ( j * LDB ) + i ] -= B[ offsetB + ( j * LDB ) + k ] * A[ offsetA + ( k * LDA ) + i ];
						}
					}
				}
			}
			return B;
		}
		// B := alpha * inv( A**T ) * B
		if ( upper ) {
			for ( j = 0; j < n; j++ ) {
				for ( i = 0; i < m; i++ ) {
					tmp = alpha * B[ offsetB + ( j * LDB ) + i ];
					for ( k = 0; k <= i - 1; k++ ) {
						tmp -= A[ offsetA + ( i * LDA ) + k ] * B[ offsetB + ( j * LDB ) + k ];
					}
					if ( nounit ) {
						tmp /= A[ offsetA + ( i * LDA ) + i ];
					}
					B[ offsetB + ( j * LDB ) + i ] = tmp;
				}
			}
			return B;
		}
		// lower
		for ( j = 0; j < n; j++ ) {
			for ( i = m - 1; i >= 0; i-- ) {
				tmp = alpha * B[ offsetB + ( j * LDB ) + i ];
				for ( k = i + 1; k < m; k++ ) {
					tmp -= A[ offsetA + ( i * LDA ) + k ] * B[ offsetB + ( j * LDB ) + k ];
				}
				if ( nounit ) {
					tmp /= A[ offsetA + ( i * LDA ) + i ];
				}
				B[ offsetB + ( j * LDB ) + i ] = tmp;
			}
		}
		return B;
	}
	// !lside
	if ( transa === 'none' ) {
		// B := alpha * B * inv( A )
		if ( upper ) {
			for ( j = 0; j < n; j++ ) {
				if ( alpha !== 1.0 ) {
					for ( i = 0; i < m; i++ ) {
						B[ offsetB + ( j * LDB ) + i ] *= alpha;
					}
				}
				for ( k = 0; k <= j - 1; k++ ) {
					if ( A[ offsetA + ( j * LDA ) + k ] !== 0.0 ) {
						for ( i = 0; i < m; i++ ) { // eslint-disable-line max-depth
							B[ offsetB + ( j * LDB ) + i ] -= A[ offsetA + ( j * LDA ) + k ] * B[ offsetB + ( k * LDB ) + i ];
						}
					}
				}
				if ( nounit ) {
					tmp = 1.0 / A[ offsetA + ( j * LDA ) + j ];
					for ( i = 0; i < m; i++ ) {
						B[ offsetB + ( j * LDB ) + i ] *= tmp;
					}
				}
			}
			return B;
		}
		// lower
		for ( j = n - 1; j >= 0; j-- ) {
			if ( alpha !== 1.0 ) {
				for ( i = 0; i < m; i++ ) {
					B[ offsetB + ( j * LDB ) + i ] *= alpha;
				}
			}
			for ( k = j + 1; k < n; k++ ) {
				if ( A[ offsetA + ( j * LDA ) + k ] !== 0 ) {
					for ( i = 0; i < m; i++ ) {
						B[ offsetB + ( j * LDB ) + i ] -= A[ offsetA + ( j * LDA ) + k ] * B[ offsetB + ( k * LDB ) + i ];
					}
				}
			}
			if ( nounit ) {
				tmp = 1.0 / A[ offsetA + ( j * LDA ) + j ];
				for ( i = 0; i < m; i++ ) {
					B[ offsetB + ( j * LDB ) + i ] *= tmp;
				}
			}
		}
		return B;
	}
	// B := alpha * B * inv( A**T )
	if ( upper ) {
		for ( k = n - 1; k >= 0; k-- ) {
			if ( nounit ) {
				tmp = 1.0 / A[ offsetA + ( k * LDA ) + k ];
				for ( i = 0; i < m; i++ ) {
					B[ offsetB + ( k * LDB ) + i ] *= tmp;
				}
			}
			for ( j = 0; j <= k - 1; j++ ) {
				if ( A[ offsetA + ( k * LDA ) + j ] !== 0 ) {
					tmp = A[ offsetA + ( k * LDA ) + j ];
					for ( i = 0; i < m; i++ ) {
						B[ offsetB + ( j * LDB ) + i ] -= tmp * B[ offsetB + ( k * LDB ) + i ];
					}
				}
			}
			if ( alpha !== 1.0 ) {
				for ( i = 0; i < m; i++ ) {
					B[ offsetB + ( k * LDB ) + i ] *= alpha;
				}
			}
		}
		return B;
	}
	// lower
	for ( k = 0; k < n; k++ ) {
		if ( nounit ) {
			tmp = 1.0 / A[ offsetA + ( k * LDA ) + k ];
			for ( i = 0; i < m; i++ ) {
				B[ offsetB + ( k * LDB ) + i ] *= tmp;
			}
		}
		for ( j = k + 1; j < n; j++ ) {
			if ( A[ offsetA + ( k * LDA ) + j ] !== 0 ) {
				tmp = A[ offsetA + ( k * LDA ) + j ];
				for ( i = 0; i < m; i++ ) {
					B[ offsetB + ( j * LDB ) + i ] -= tmp * B[ offsetB + ( k * LDB ) + i ];
				}
			}
		}
		if ( alpha !== 1.0 ) {
			for ( i = 0; i < m; i++ ) {
				B[ offsetB + ( k * LDB ) + i ] *= alpha;
			}
		}
	}
	return B;
}


// EXPORTS //

module.exports = dtrsm;
