'use strict';

// MODULES //

var abs = require( '@stdlib/math/base/special/abs' );
var sqrt = require( '@stdlib/math/base/special/sqrt' );
var pow = require( '@stdlib/math/base/special/pow' );
var copysign = require( '@stdlib/math/base/special/copysign' );


// MAIN //

/**
* Construct a Givens rotation from two values `a` and `b`.
*
* @private
* @param {number} a - first value
* @param {number} b - second value
* @param {Float32Array} out - destination array
* @param {number} stride - index increment
* @param {number} offset - starting index
* @returns {Float32Array} destination array
*
*
* @example
* var Float32Array = require( '@stdlib/array/float32' );
* var out = srotg( 0.0, 2.0, new Float32Array( 4 ), 1, 0 );
*  // returns <Float32Array>[ 2.0, 1.0, 0.0, 1.0 ]
*/
function srotg( a, b, out, stride, offset ) {
	var scale;
	var roe;
	var r;
	var c;
	var s;
	var z;

	roe = b;
	if ( abs( a ) > abs( b ) ) {
		roe = a;
	}
	scale = abs( a ) + abs( b );
	if ( scale === 0.0 ) {
		c = 1.0;
		s = 0.0;
		r = 0.0;
		z = 0.0;
	} else {
		r = scale * sqrt( pow( a/scale, 2 ) + pow( b/scale, 2 ) );
		r = copysign( 1.0, roe ) * r;
		c = a / r;
		s = b / r;
		z = 1.0;
		if ( abs( a ) > abs( b ) ) {
			z = s;
		}
		if ( abs( b ) >= abs( a ) && c !== 0.0 ) {
			z = 1.0 / c;
		}
	}
	a = r;
	b = z;
	out[ offset ] = a;
	out[ offset + stride ] = b;
	out[ offset + ( 2 * stride ) ] = c;
	out[ offset + ( 3 * stride ) ] = s;
	return out;
}


// EXPORTS //

module.exports = srotg;
