// The compiler throws an error if the function is provided an unsupported number of arguments...
{
	const A = new Float64Array( [ 1.0, 2.0, 3.0, 4.0 ] );
	const B = new Float64Array( 4 );

	dlacpy(); // $ExpectError
	dlacpy( 'row-major' ); // $ExpectError
	dlacpy( 'row-major', 'all' ); // $ExpectError
	dlacpy( 'row-major', 'all', 5 ); // $ExpectError
	dlacpy( 'row-major', 'all', 5, 2 ); // $ExpectError
	dlacpy( 'row-major', 'all', 5, 2, A ); // $ExpectError
	dlacpy( 'row-major', 'all', 5, 2, A, 2 ); // $ExpectError
	dlacpy( 'row-major', 'all', 5, 2, A, 2, B ); // $ExpectError
	dlacpy( 'row-major', 'all', 5, 2, A, 2, B, 2, 2 ); // $ExpectError
}

// Attached to main export is an `ndarray` method which returns a Float64Array...
{
	const A = new Float64Array( [ 1.0, 2.0, 3.0, 4.0 ] );
	const B = new Float64Array( 4 );

	dlacpy.ndarray( 'row-major', 'all', 2, 2, A, 2, 0, B, 2, 0 ); // $ExpectType Float64Array
}

