// The compiler throws an error if the `ndarray` method is provided an unsupported number of arguments...
{
	const A = new Float64Array( [ 1.0, 2.0, 3.0, 4.0 ] );
	const B = new Float64Array( 4 );

	dlacpy.ndarray(); // $ExpectError
	dlacpy.ndarray( 'row-major' ); // $ExpectError
	dlacpy.ndarray( 'row-major', 'all' ); // $ExpectError
	dlacpy.ndarray( 'row-major', 'all', 5 ); // $ExpectError
	dlacpy.ndarray( 'row-major', 'all', 5, 2 ); // $ExpectError
	dlacpy.ndarray( 'row-major', 'all', 5, 2, A ); // $ExpectError
	dlacpy.ndarray( 'row-major', 'all', 5, 2, A, 2 ); // $ExpectError
	dlacpy.ndarray( 'row-major', 'all', 5, 2, A, 2, 0 ); // $ExpectError
	dlacpy.ndarray( 'row-major', 'all', 5, 2, A, 2, 0, B ); // $ExpectError
	dlacpy.ndarray( 'row-major', 'all', 5, 2, A, 2, 0, B, 2 ); // $ExpectError
	dlacpy.ndarray( 'row-major', 'all', 5, 2, A, 2, 0, B, 2, 0, 10 ); // $ExpectError
}
