'use strict';

// MODULES //

var min = require( '@stdlib/math/base/special/min' );


// MAIN //

/**
* Copies all or part of a two-dimensional matrix `A` to another matrix `B`.
*
* @private
* @param {order} order - storage layout of `A` and `B`
* @param {string} uplo - specifies whether the upper or lower triangular part of the symmetric matrix `A` is supplied
* @param {NonNegativeInteger} M - number of rows in matrix `A`
* @param {NonNegativeInteger} N - number of columns in matrix `A`
* @param {Float64Array} A - input matrix
* @param {PositiveInteger} LDA - stride of the first dimension of `A` (a.k.a., leading dimension of the matrix `A`)
* @param {PositiveInteger} offsetA - index offset for `A`
* @param {Float64Array} B - destination matrix
* @param {PositiveInteger} LDB - stride of the first dimension of `B` (a.k.a., leading dimension of the matrix `B`)
* @param {PositiveInteger} offsetB - index offset for `B`
* @returns {Float64Array} `B`
*
* @example
* var Float64Array = require( '@stdlib/array/float64' );
*
* var A = new Float64Array( [ 1.0, 2.0, 3.0, 4.0 ] );
* var B = new Float64Array( 4 );
*
* dlacpy( 'row-major', 'all', 2, 2, A, 2, 0, B, 2, 0 );
* // B => <Float64Array>[ 1.0, 2.0, 3.0, 4.0 ]
*/
function dlacpy( order, uplo, M, N, A, LDA, offsetA, B, LDB, offsetB ) {
	var i;
	var j;

	if ( uplo === 'upper' ) {
		if ( order === 'column-major' ) {
			for ( j = 0; j < N; j++ ) {
				for ( i = 0; i < min( j, M ); i++ ) {
					B[ offsetB + ( j * LDB ) + i ] = A[ offsetA + ( j * LDA ) + i ]; // eslint-disable-line max-len
				}
			}
			return B;
		}
		// order === 'row-major'
		for ( j = 0; j < N; j++ ) {
			for ( i = 0; i < min( j, M ); i++ ) {
				B[ offsetB + ( i * LDB ) + j ] = A[ offsetA + ( i * LDA ) + j ];
			}
		}
		return B;
	}
	if ( uplo === 'lower' ) {
		if ( order === 'column-major' ) {
			for ( j = 0; j < N; j++ ) {
				for ( i = j; i < M; i++ ) {
					B[ offsetB + ( j * LDB ) + i ] = A[ offsetA + ( j * LDA ) + i ]; // eslint-disable-line max-len
				}
			}
			return B;
		}
		// order === 'row-major'
		for ( j = 0; j < N; j++ ) {
			for ( i = j; i < M; i++ ) {
				B[ offsetB + ( i * LDB ) + j ] = A[ offsetA + ( i * LDA ) + j ];
			}
		}
		return B;
	}
	// All of the matrix `A`
	if ( order === 'column-major' ) {
		for ( j = 0; j < N; j++ ) {
			for ( i = 0; i < M; i++ ) {
				B[ offsetB + ( j * LDB ) + i ] = A[ offsetA + ( j * LDA ) + i ];
			}
		}
		return B;
	}
	// order === 'row-major'
	for ( j = 0; j < N; j++ ) {
		for ( i = 0; i < M; i++ ) {
			B[ offsetB + ( i * LDB ) + j ] = A[ offsetA + ( i * LDA ) + j ];
		}
	}
	return B;
}


// EXPORTS //

module.exports = dlacpy;
