// TypeScript Version: 4.1

/**
* Determines double precision machine parameters.
*
* @param cmach - specifies the value to be returned
* @returns machine parameter
*
* @example
* var out = dlamch( 'E' );
* // returns ~1.1102230246251565E-016
*
* out = dlamch( 'S' );
* // returns ~2.2250738585072014E-308
*
* out = dlamch( 'B' );
* // returns 2.0
*/
declare function dlamch( x: string ): number;


// EXPORTS //

export = dlamch;
