/* eslint-disable max-len */

'use strict';

// MODULES //

var dlassq = require( '@stdlib/lapack/base/dlassq' ).ndarray;
var lowercase = require( '@stdlib/string/base/lowercase' );
var sqrt = require( '@stdlib/math/base/special/sqrt' );
var Float64Array = require( '@stdlib/array/float64' );
var abs = require( '@stdlib/math/base/special/abs' );
var min = require( '@stdlib/math/base/special/min' );
var isnan = require( '@stdlib/assert/is-nan' );


// MAIN //

/**
* Returns the value of one norm, or the Forbenius norm, or the infinity norm, or the element of largest absolute value of a real matrix A.
*
* @private
* @param {string} order - storage layout
* @param {string} norm - specifies the value to be returned
* @param {NonNegativeInteger} M - number of rows of `A`
* @param {NonNegativeInteger} N - number of columns of `A`
* @param {Float64Array} A - input array
* @param {integer} strideA1 - stride of the first dimension of `A`
* @param {integer} strideA2 - stride of the second dimension of `A`
* @param {PositiveInteger} offsetA - starting index for `A`
* @param {Float64Array} work - workspace array
* @param {integer} strideW - stride length for `work`
* @param {PositiveInteger} offsetW - starting index for `work`
* @returns {number} matrix norm
*
* @example
* var Float64Array = require( '@stdlib/array/float64' );
*
* var A = new Float64Array( [ 1.0, 2.0, 3.0, 4.0 ] );
* var work = new Float64Array( 2 );
*
* var out = dlange( 'row-major', 'M', 2, 2, A, 2, 1, 0, work, 1, 0 );
* // out => 4.0
*/
function dlange( order, norm, M, N, A, strideA1, strideA2, offsetA, work, strideW, offsetW ) { // eslint-disable-line max-params
	var scale;
	var value;
	var sum;
	var tmp;
	var out;
	var sa;
	var i;
	var j;

	norm = lowercase( norm );
	if ( min( M, N ) === 0.0 ) {
		value = 0.0;
	} else if ( norm === 'm' ) {
		// Find max( abs( A[ i, j ] ) )
		value = 0.0;
		if ( order === 'column-major' ) {
			for ( j = 0; j < N; j++ ) {
				sa = offsetA + ( j * strideA1 );
				for ( i = 0; i < M; i++ ) {
					tmp = abs( A[ sa + ( i * strideA2 ) ] );
					if ( value <= tmp || isnan( tmp ) ) {
						value = tmp;
					}
				}
			}
			return value;
		}
		// order === 'row-major'
		for ( j = 0; j < N; j++ ) {
			sa = offsetA + ( j * strideA2 );
			for ( i = 0; i < M; i++ ) {
				tmp = abs( A[ sa + ( i * strideA1 ) ] );
				if ( value <= tmp || isnan( tmp ) ) {
					value = tmp;
				}
			}
		}
		return value;
	}
	if ( norm === 'o' || norm === '1' ) {
		// Find norm1( A )
		value = 0.0;
		if ( order === 'column-major' ) {
			for ( j = 0; j < N; j++ ) {
				sum = 0.0;
				sa = offsetA + ( j * strideA2 );
				for ( i = 0; i < M; i++ ) {
					sum += abs( A[ sa + ( i * strideA1 ) ] );
				}
				if ( value < sum || isnan( sum ) ) {
					value = sum;
				}
			}
			return value;
		}
		// order === 'row-major'
		for ( j = 0; j < N; j++ ) {
			sum = 0.0;
			sa = offsetA + ( j * strideA2 );
			for ( i = 0; i < M; i++ ) {
				sum += abs( A[ sa + ( i * strideA1 ) ] );
			}
			if ( value < sum || isnan( sum ) ) {
				value = sum;
			}
		}
		return value;
	}
	if ( norm === 'i' ) {
		// Find normI( A )
		for ( i = 0; i < M; i++ ) {
			work[ offsetW + ( i * strideW ) ] = 0.0;
		}
		if ( order === 'column-major' ) {
			for ( j = 0; j < N; j++ ) {
				sa = offsetA + ( j * strideA2 );
				for ( i = 0; i < M; i++ ) {
					work[ offsetW + ( i * strideW ) ] += abs( A[ sa + ( i * strideA1 ) ] );
				}
			}
		} else { // order === 'row-major'
			for ( j = 0; j < N; j++ ) {
				sa = offsetA + ( j * strideA2 );
				for ( i = 0; i < M; i++ ) {
					work[ offsetW + ( i * strideW ) ] += abs( A[ sa + ( i * strideA1 ) ] );
				}
			}
		}
		value = 0.0;
		for ( i = 0; i < M; i++ ) {
			tmp = work[ offsetW + ( i * strideW ) ];
			if ( value < tmp || isnan( tmp ) ) {
				value = tmp;
			}
		}
		return value;
	}
	if ( norm === 'f' ) {
		// Find normF( A )
		scale = 0.0;
		sum = 1.0;
		out = new Float64Array( 2 );
		if ( order === 'column-major' ) {
			for ( j = 0; j < N; j++ ) {
				out = dlassq( M, A, strideA2, offsetA + ( j * strideA1 ), scale, sum, out, 1, 0 );
			}
			value = scale * sqrt( out[ 1 ] );
			return value;
		}
		// order === 'row-major'
		for ( i = 0; i < M; i++ ) {
			out = dlassq( N, A, strideA1, offsetA + ( i * strideA2 ), scale, sum, out, 1, 0 );
		}
	}
	return value;
}


// EXPORTS //

module.exports = dlange;
