'use strict';

// MODULES //

var isRowMajor = require( '@stdlib/ndarray/base/assert/is-row-major' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var sqrt = require( '@stdlib/math/base/special/sqrt' );
var dgemv = require( '@stdlib/blas/base/dgemv' ).ndarray;
var dscal = require( '@stdlib/blas/base/dscal' ).ndarray;
var ddot = require( '@stdlib/blas/base/ddot' ).ndarray;


// MAIN //

/**
* Computes the Cholesky factorization of a real symmetric positive definite matrix `A`.
*
* @private
* @param {string} uplo - specifies whether to copy the upper or lower triangular/trapezoidal part of matrix `A`
* @param {NonNegativeInteger} N - order of matrix `A`
* @param {Float64Array} A - input symmetric positive definite matrix
* @param {integer} strideA1 - stride of the first dimension of `A`
* @param {integer} strideA2 - stride of the second dimension of `A`
* @param {NonNegativeInteger} offsetA - starting index for `A`
* @returns {integer} status code
*
* @example
* var Float64Array = require( '@stdlib/array/float64' );
*
* var A = new Float64Array( [ 1.0, 0.0, 0.0, 4.0 ] );
*
* var out = dpotf2( 'upper', 2, A, 2, 1, 0 );
* // returns 0
*/
function dpotf2( uplo, N, A, strideA1, strideA2, offsetA ) {
	var upper;
	var ajj;
	var j;

	upper = ( uplo === 'upper' );

	if ( N === 0 ) {
		return 0;
	}
	if ( upper ) {
		if ( isRowMajor( [ strideA1, strideA2 ] ) ) {
			// Compute the Cholesky factorization A = U^T*U.
			for ( j = 0; j < N; j++ ) {
				// Compute U( j, j ) and test for non-positive-definiteness...
				ajj = A[ offsetA + ( j * strideA1 ) + ( j * strideA2 ) ] - ddot( j, A, strideA1, offsetA + ( j * strideA2 ), A, strideA1, offsetA + ( j * strideA2 ) );
				if ( ajj <= 0.0 || isnan( ajj ) ) {
					A[ offsetA + ( j * strideA1 ) + ( j * strideA2 ) ] = ajj;
					return j;
				}
				ajj = sqrt( ajj );
				A[ offsetA + ( j * strideA1 ) + ( j * strideA2 ) ] = ajj;

				// Compute elements j+1:N of row j...
				if ( j < N - 1 ) {
					dgemv( 'transpose', j, N - j - 1, -1.0, A, strideA1, strideA2, offsetA + ( ( j + 1 ) * strideA2 ), A, strideA1, offsetA + ( j * strideA2 ), 1.0, A, strideA1, offsetA + ( j * strideA1 ) + ( ( j + 1 ) * strideA2 ) );
					dscal( N - j - 1, 1.0 / ajj, A, strideA1, offsetA + ( j * strideA1 ) + ( ( j + 1 ) * strideA2 ) );
				}
			}
			return 0;
		}
		// column-major
		for ( j = 0; j < N; j++ ) {
			// Compute U( j, j ) and test for non-positive-definiteness...
			ajj = A[ offsetA + ( j * strideA2 ) + ( j * strideA1 ) ] - ddot( j, A, strideA2, offsetA + ( j * strideA1 ), A, strideA2, offsetA + ( j * strideA1 ) );
			if ( ajj <= 0.0 || isnan( ajj ) ) {
				A[ offsetA + ( j * strideA2 ) + ( j * strideA1 ) ] = ajj;
				return j;
			}
			ajj = sqrt( ajj );
			A[ offsetA + ( j * strideA2 ) + ( j * strideA1 ) ] = ajj;

			// Compute elements j+1:N of row j...
			if ( j < N - 1 ) {
				dgemv( 'transpose', j, N - j - 1, -1.0, A, strideA2, strideA1, offsetA + ( ( j + 1 ) * strideA1 ), A, strideA2, offsetA + ( j * strideA1 ), 1.0, A, strideA2, offsetA + ( j * strideA2 ) + ( ( j + 1 ) * strideA1 ) );
				dscal( N - j - 1, 1.0 / ajj, A, strideA2, offsetA + ( j * strideA2 ) + ( ( j + 1 ) * strideA1 ) );
			}
		}
		return 0;
	}
	// Lower
	if ( isRowMajor( [ strideA1, strideA2 ] ) ) {
		// Compute the Cholesky factorization A = L*L^T.
		for ( j = 0; j < N; j++ ) {
			// Compute L( j, j ) and test for non-positive-definiteness...
			ajj = A[ offsetA + ( j * strideA1 ) + ( j * strideA2 ) ] - ddot( j, A, strideA2, offsetA + ( j * strideA1 ), A, strideA2, offsetA + ( j * strideA1 ) );
			if ( ajj <= 0.0 || isnan( ajj ) ) {
				A[ offsetA + ( j * strideA1 ) + ( j * strideA2 ) ] = ajj;
				return j;
			}
			ajj = sqrt( ajj );
			A[ offsetA + ( j * strideA1 ) + ( j * strideA2 ) ] = ajj;

			// Compute elements j+1:N of column j...
			if ( j < N - 1 ) {
				dgemv( 'no-transpose', N - j - 1, j, -1.0, A, strideA1, strideA2, offsetA + ( ( j + 1 ) * strideA1 ) + ( j * strideA2 ), A, strideA1, offsetA + ( j * strideA1 ), 1.0, A, strideA1, offsetA + ( ( j + 1 ) * strideA1 ) + ( j * strideA2 ) );
				dscal( N - j - 1, 1.0 / ajj, A, strideA1, offsetA + ( ( j + 1 ) * strideA1 ) + ( j * strideA2 ) );
			}
		}
		return 0;
	}
	// column-major
	for ( j = 0; j < N; j++ ) {
		// Compute L( j, j ) and test for non-positive-definiteness...
		ajj = A[ offsetA + ( j * strideA2 ) + ( j * strideA1 ) ] - ddot( j, A, strideA1, offsetA + ( j * strideA2 ), A, strideA1, offsetA + ( j * strideA2 ) );
		if ( ajj <= 0.0 || isnan( ajj ) ) {
			A[ offsetA + ( j * strideA2 ) + ( j * strideA1 ) ] = ajj;
			return j;
		}
		ajj = sqrt( ajj );
		A[ offsetA + ( j * strideA2 ) + ( j * strideA1 ) ] = ajj;

		// Compute elements j+1:N of column j...
		if ( j < N ) {
			dgemv( 'no-transpose', N - j - 1, j, -1.0, A, strideA1, strideA2, offsetA + ( ( j + 1 ) * strideA2 ), A, strideA1, offsetA + ( j * strideA2 ), 1.0, A, strideA1, offsetA + ( ( j + 1 ) * strideA2 ) + ( j * strideA1 ) );
			dscal( N - j - 1, 1.0 / ajj, A, strideA1, offsetA + ( ( j + 1 ) * strideA2 ) + ( j * strideA1 ) );
		}
	}
	return 0;
}


// EXPORTS //

module.exports = dpotf2;
