#include "stdlib/math/base/special/round.h"
#include "stdlib/math/base/special/abs.h"
#include "stdlib/math/base/special/floor.h"
#include "stdlib/math/base/special/ceil.h"
#include "stdlib/math/base/assert/is_negative_zero.h"

/**
* Rounds a numeric value to the nearest integer.
*
* @param x    input value
* @return	  output value
*
* @example
* double out = stdlib_base_round( -4.2 );
* // returns -4.0
*/
double stdlib_base_round( const double x ) {
    double fx;
    double cx;
    if ( stdlib_base_is_negative_zero( x ) ) {
        return x;
    }
    fx = stdlib_base_floor( x );
    cx = stdlib_base_ceil( x );
    if ( stdlib_base_abs( fx - x) >= stdlib_base_abs( cx - x ) ) {
        return cx;
    }  else {
        return fx;
    }
}
